//工具类
var jtool = (function () {

    //向客户端发送信息函数
    function send(res, data) {
        var query = res.query;

        //jsonp方式
        if (query && query.callback !== undefined) {
            res.send(query.callback(data));
        }
        else {
            res.send(data);
        }
    }

    return {
        send: send
    };

})();

module.exports = jtool;